mapper.update:
UPDATE bg_event
event_name = #{eventName}, region = #{region}, venue = #{venue}, start_date = #{startDate}, end_date = #{endDate}, registration_start = #{registrationStart}, registration_end = #{registrationEnd}, contact_phone = #{contactPhone}, image_url = #{imageUrl}, event_status = #{eventStatus}, prize_info = #{prizeInfo}, entry_fee = #{entryFee}, max_participants = #{maxParticipants}, event_description = #{eventDescription},
WHERE event_id = #{eventId}
