mapper.select:
SELECT * FROM bg_participant
WHERE event_id = #{eventId} AND event_type = #{eventType}
ORDER BY uploader_id ASC

mapper.update:
UPDATE bg_participant
event_type = #{eventType}, age_group = #{ageGroup}, skill_level = #{skillLevel}, team_name = #{teamName}, participant1_name = #{participant1Name}, participant1_birth = #{participant1Birth}, participant1_phone = #{participant1Phone}, participant2_name = #{participant2Name}, participant2_birth = #{participant2Birth}, participant2_phone = #{participant2Phone},
WHERE participant_id = #{participantId}
