mapper.update:
UPDATE bg_participant
participant_name = #{participantName}, phone = #{phone}, email = #{email}, participant_role = #{participantRole}, club_name = #{clubName}, gender = #{gender}, birth_date = #{birthDate}, skill_level = #{skillLevel}, emergency_contact = #{emergencyContact},
WHERE participant_id = #{participantId}

mapper.select:
SELECT p.* FROM bg_participant p
JOIN bg_event_participant ep ON p.participant_id = ep.participant_id
WHERE ep.event_id = #{eventId}
